</template>

<script>
import CommentBox from "./Comments/CommentBox.vue";
import CommentItem from "./Comments/CommentItem.vue";

export default {
    components: {
        CommentBox,
        CommentItem,
    },
    props: {
        postSlug: {
            type: String,
            required: true
        },
        postId: {
            type: String,
            required: true
        },
    },
    data() {
        return {
            comments: [],
            count: 0,
            user: null
        }
    },
    created() {
        this.checkAuth();
        this.getComments();
    },
    methods: {
        getComments() {
            axios.get('/comments/post/' + this.postId).then(response => {
                this.comments = response.data.tree;
                this.count = response.data.count;
            });
        },
        checkAuth() {
            axios.get('/is_login')
                .then(response => {
                    if (response.data.code === 200) {
                        this.user = response.data.data;
                    } else {
                        this.user = null;
                    }
                })
                .catch(error => {
                    this.user = null;
                });
        },
    }
};

</script>

